import React, { useState } from 'react';
import axios from 'axios';

function QuestionAsker({ sessionId }) {
    const [question, setQuestion] = useState('');
    const [answer, setAnswer] = useState('');

    const handleQuestionChange = (event) => {
        setQuestion(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/ask_question', {
                session_id: sessionId,
                question,
            });
            setAnswer(response.data.answer);
        } catch (error) {
            console.error('Error asking question:', error);
            alert('Failed to ask question');
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" value={question} onChange={handleQuestionChange} placeholder="Ask a question" />
                <button type="submit">Ask</button>
            </form>
            <div>
                <strong>Answer:</strong>
                <p>{answer}</p>
            </div>
        </div>
    );
}

export default QuestionAsker;
