// src/components/NavBar.js
import React from 'react';
import './NavBar.css';
import PdfUploader from './PdfUploader';
import ChatApplication from './ChatApplication';


function NavBar({ setSessionId, handlePdfUpload }) {
    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            handlePdfUpload(file);
        }
    };

    const clearChatHistory = () => {
        setChatHistory([]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        // Add your logic for submitting the file if necessary.
        // Since we're using handlePdfUpload to set the file immediately on file selection,
        // you may not need any submit logic here.
    };

    return (
        <div className="nav-bar">
            <h1>Chat with any PDF</h1>
            <p>Join millions of students, researchers and professionals to instantly answer questions and understand research with AI</p>
            {/* <form onSubmit={handleSubmit}>
                <input
                    type="file"
                    onChange={handleFileChange}
                    accept=".pdf"
                    className="file-upload"
                />
            </form> */}
            {/* <form onSubmit={handleSubmit}>
            <input type="file" onChange={handleFileChange} accept=".pdf"  className="file-upload"/>
            <button type="submit" className="file-upload">Upload PDF</button>
        </form> */}
        <PdfUploader setSessionId={setSessionId} onUploadSuccess={clearChatHistory} />
        
        </div>
    );
}

export default NavBar;
