import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import PdfUploader from './PdfUploader';
import './ChatApplication.css';  // Ensure CSS for basic styling

function ChatApplication() {
    const [sessionId, setSessionId] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef(null);  // Reference to the end of messages

    useEffect(() => {
        if (!sessionId) {
            // Automatically fetch a new session when the component mounts
            const getSession = async () => {
                const response = await axios.get('http://localhost:5000/get_session');
                setSessionId(response.data.session_id);
            };
            getSession();
        }
    }, [sessionId]);

    useEffect(() => {
        // Scroll to the bottom every time chatHistory changes
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatHistory]);

    const handleSendMessage = async (event) => {
        event.preventDefault(); // Prevent default form submission behavior
        if (inputMessage.trim()) {
            setIsLoading(true);
            setChatHistory([...chatHistory, { role: 'user', content: inputMessage }]);
            try {
                const response = await axios.post('http://localhost:5000/ask_question', {
                    session_id: sessionId,
                    question: inputMessage,
                });
                setChatHistory(currentHistory => [...currentHistory, { role: 'assistant', content: response.data.answer }]);
            } catch (error) {
                console.error('Error sending message:', error);
                setChatHistory(currentHistory => [...currentHistory, { role: 'assistant', content: "Failed to get response." }]);
            }
            setInputMessage('');  // Clear the input after sending
            setIsLoading(false);
        }
    };

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            handleSendMessage(event);
        }
    };

    const clearChatHistory = () => {
        setChatHistory([]);  // Clear the chat history
    };

    return (
        <div className="chat-container">
            <PdfUploader setSessionId={setSessionId} onUploadSuccess={clearChatHistory} />
            <div className="chat-history">
                {chatHistory.map((msg, index) => (
                    <div key={index} className={`message ${msg.role}`}>
                        {msg.content}
                    </div>
                ))}
                {isLoading && <div className="message system">Generating Response...</div>}
                <div ref={messagesEndRef} /> {/* Empty div for auto-scrolling */}
            </div>
            <form className="input-group" onSubmit={handleSendMessage}>
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    onKeyDown={handleKeyDown}
                    placeholder="Type your message and press Enter"
                />
                <button type="submit" className="send-button">
                    &#x2B06; {/* Unicode character for upward arrow */}
                </button>
            </form>
        </div>
    );
}

export default ChatApplication;
