// src/components/ChatApplication.js
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import PdfUploader from './PdfUploader';
import { Worker, Viewer } from '@react-pdf-viewer/core';
import '@react-pdf-viewer/core/lib/styles/index.css';
import '@react-pdf-viewer/default-layout/lib/styles/index.css';
import './ChatApplication.css';

function ChatApplication() {
    const [sessionId, setSessionId] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [pdfFile, setPdfFile] = useState(null);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        if (!sessionId) {
            const getSession = async () => {
                const response = await axios.get('http://localhost:5000/get_session');
                setSessionId(response.data.session_id);
            };
            getSession();
        }
    }, [sessionId]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatHistory]);

    const handleSendMessage = async (event) => {
        event.preventDefault();
        if (inputMessage.trim()) {
            setIsLoading(true);
            setChatHistory([...chatHistory, { role: 'user', content: inputMessage }]);
            try {
                const response = await axios.post('http://localhost:5000/ask_question', {
                    session_id: sessionId,
                    question: inputMessage,
                });
                setChatHistory(currentHistory => [...currentHistory, { role: 'assistant', content: response.data.answer }]);
            } catch (error) {
                console.error('Error sending message:', error);
                setChatHistory(currentHistory => [...currentHistory, { role: 'assistant', content: "Failed to get response." }]);
            }
            setInputMessage('');
            setIsLoading(false);
        }
    };

    const clearChatHistory = () => {
        setChatHistory([]);
    };
    
    const handlePdfUpload = (file) => {
        // Set session ID to null, clear chat history, etc.
        setSessionId(null); // Reset the session ID if needed
        clearChatHistory(); // Clear the chat history
        clearChatHistory(); // Clear the chat history
        alert('PDF uploaded successfully!');
    };

    // const handlePdfUpload = (file) => {
    //     const fileURL = URL.createObjectURL(file);
    //     setPdfFile(fileURL); // Create a URL for the uploaded file
    //     setSessionId(null); // Reset the session ID if needed
    //     clearChatHistory(); // Clear the chat history
    //     alert('PDF uploaded successfully!'); // Add this line to show popup
    // };

    const handleFileSelected = (event) => {
        const file = event.target.files[0];
        if (file) {
            setPdfFile(URL.createObjectURL(file)); // Set file for PDF viewer
            handlePdfUpload(file); // Trigger the upload handling function which should show the alert
        }
    };

    return (
        <div className="app-container">
        {/* <NavBar setSessionId={setSessionId} handlePdfUpload={handlePdfUpload} /> */}
        {/* <NavBar onFileSelect={handleFileSelected} />
         */}
        <div className="nav-bar">
            <h1>Chat with any PDF</h1>
            <p>Upload your pdf and ask questions related to the pdf from the  AI chatbot </p>

        <PdfUploader setSessionId={setSessionId} onPdfUpload={handlePdfUpload} onUploadSuccess={clearChatHistory}  />   

        {/* <PdfUploader setSessionId={setSessionId} onUploadSuccess={clearChatHistory} /> */}
        
        </div>
        <div className={pdfFile ? "split-container" : "chat-fullscreen"}>
            
  
            <div className="chat-container">
                {/* <PdfUploader setSessionId={setSessionId} onUploadSuccess={clearChatHistory} /> */}
                <div className="chat-history">
                    {chatHistory.map((msg, index) => (
                        <div key={index} className={`message ${msg.role}`}>
                            {msg.content}
                        </div>
                    ))}
                    {isLoading && <div className="message system">Generating Response...</div>}
                    <div ref={messagesEndRef} /> {/* Empty div for auto-scrolling */}
                </div>
                <form className="input-group" onSubmit={handleSendMessage}>
                    <input
                        type="text"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && handleSendMessage(e)}
                        placeholder="Type your message and press Enter"
                    />
                    <button type="submit" className="send-button">
                        &#x2B06; {/* Unicode character for upward arrow */}
                    </button>
                </form>
            </div>
            {pdfFile && (
          <div className="pdf-container">
            <Worker>
              <Viewer fileUrl={pdfFile} />
            </Worker>
          </div>
        )}
      </div>
    </div>
    );
}

export default ChatApplication;
