// src/components/NavBar.js
import React from 'react';
import './NavBar.css';

function NavBar({ onFileSelect }) {
  return (
    <div className="nav-bar">
      <h1>Chat with any PDF</h1>
      <p>Join millions of students, researchers and professionals to instantly answer questions and understand research with AI</p>
      <input
        type="file"
        onChange={onFileSelect}
        accept=".pdf"
        className="file-upload"
      />
    </div>
  );
}

export default NavBar;
